const SpreadSpin = (
  number: number,
  spX: number,
  spY: number,
  rotY: number,
  rotZ: number
) => keyframes`
    0% {
      transform: rotateZ(${number * 90 - 0}deg) translate(0px, 0px);
    }
    30% {
      transform: rotateZ(${
        number * 90 - 0
      }deg) translate(${spX}px, ${spY}px) rotateY(${rotY}deg);
    }
    70% {
      transform: rotateZ(${
        number * 90 - rotZ
      }deg) translate(${spX}px, ${spY}px) rotateY(${rotY}deg);
    }
    100% {
      transform: rotateZ(${number * 90 - rotZ}deg) translate(0px, 0px);
    }
`;

const Spin = () => keyframes`
    0% {
      transform: rotateZ(0deg);
    }
    30% {
      transform: rotateZ(45deg);
    }
    70% {
      transform: rotateZ(135deg);
    }
    100% {
      transform: rotateZ(180deg);
    }
`;

const Hanabira = styled.div<AtomsProps>`
  ${tw`absolute inset-0 m-auto`}
  width: 600px;
  height: 600px;
  transition: 1s;

  ${({ number }) =>
    css`
      transform: rotateZ(${number * 90}deg);
    `}

  ${({ select }) =>
    select &&
    css`
      svg {
        ${tw`text-primary`}
      }
    `}

    ${({ number, loadAnimation }) =>
    loadAnimation &&
    css`
      animation: ${SpreadSpin(number, 0, -160, 180, 90)} 3s
        cubic-bezier(0.9, 0, 0.1, 1);
    `}

    ${({ select, number, animation }) =>
    select &&
    animation &&
    css`
      animation: ${SpreadSpin(number, 0, -160, 180, 90)} 3s
        cubic-bezier(0.9, 0, 0.1, 1);
    `}
`;

const Mannaka = styled.div<AtomsProps>`
  ${tw`absolute inset-0 m-auto`}
  width: 600px;
  height: 600px;
  transition: 1s;

  .inner {
    width: 600px;
    height: 600px;
    transition: 1s;
    transform-origin: 323.836px 323.836px;
    transform: rotateZ(90deg);
  }

  ${({ number }) =>
    css`
      transform: rotateZ(${number * 90}deg);
    `}

  ${({ select }) =>
    select &&
    css`
      svg {
        ${tw`text-primary`}
      }
    `}

    ${({ number, loadAnimation }) =>
    loadAnimation &&
    css`
      animation: ${SpreadSpin(number, 20, 20, 0, -90)} 3s
        cubic-bezier(0.9, 0, 0.1, 1);

      .inner {
        animation: ${Spin} 3s cubic-bezier(0.9, 0, 0.1, 1);
      }
    `}

    ${({ select, number, animation }) =>
    select &&
    animation &&
    css`
      animation: ${SpreadSpin(number, 20, 20, 0, -90)} 3s
        cubic-bezier(0.9, 0, 0.1, 1);

      .inner {
        animation: ${Spin} 3s cubic-bezier(0.9, 0, 0.1, 1);
      }
    `}
`;

